func Test_PositiveBF(t *testing.T) {
	got, err := interpret_bf(strings.NewReader(helloworld), "")
	want := "Hello World!\n"
	if got != want {
		t.Errorf("got %q, wanted %q and found error %q", got, want, err)
	}
	got, err = interpret_bf(strings.NewReader(WorldLine), "")
	want = "WorldLine Ingenico"
	if got != want {
		t.Errorf("got %q, wanted %q and found error %q", got, want, err)
	}
}
func Test_PutChar(t *testing.T) {
	got, err := interpret_bf(strings.NewReader(Putchar), "m")
	want := "Testing Putchar"
	if got != want {
		t.Errorf("got %q, wanted %q and found error %q", got, want, err)
	}
}
func Test_largeBF(t *testing.T) {
	got, err := interpret_bf(strings.NewReader(largeone), "")
	want := "The Go programming language is an open source project to make programmers more productive.\n\nGo is expressive, concise, clean, and efficient. Its concurrency mechanisms make it easy to write programs that get the most out of multicore and networked machines, while its novel type system enables flexible and modular program construction. Go compiles quickly to machine code yet has the convenience of garbage collection and the power of run-time reflection. It's a fast, statically typed, compiled language that feels like a dynamically typed, interpreted language.\n\n"
	if !strings.Contains(got, want) {
		t.Errorf("got %q, wanted %q and found error %q", got, want, err)
	}
}